def add_furigana(text):
    result = kks.convert(text)
    return ''.join([f"<ruby>{item['orig']}<rt>{item['hira']}</rt></ruby>" if '\u4e00' <= item['orig'] <= '\u9fff' else item['orig'] for item in result])

